import static org.junit.Assert.*; import org.junit.Test; public class BasicTest { @Test public void testPlayerExists() { try { Player p = new Player(); assertNotNull("Player object should not be null", p); assertNotNull("Player name should not be null", p.getName()); assertTrue("Player level should be >= 0", p.getLevel() >= 0); } catch (Exception e) { fail("Player class or constructor does not exist or failed: " + e.getMessage()); } } @Test public void testVloggerExists() { try { Vlogger v = new Vlogger(); assertNotNull("Vlogger object should not be null", v); assertNotNull("Vlogger name should not be null", v.getName()); assertTrue("Vlogger yearJoined should be >= 0", v.getYearJoined() > 0); } catch (Exception e) { fail("Vlogger class or constructor does not exist or failed: " + e.getMessage()); } } @Test public void testPaintingExists() { try { Painting paint = new Painting(); assertNotNull("Painting object should not be null", paint); assertNotNull("Painting title should not be null", paint.getTitle()); assertTrue("Painting year should be >= 0", paint.getYear() > 0); } catch (Exception e) { fail("Painting class or constructor does not exist or failed: " + e.getMessage()); } } }
